import React, { Component } from 'react';
import './ArtDisplay.css';
import { SlideToggle } from 'react-slide-toggle';

class Description extends Component {
  constructor() {
    super();
    this.handleLink = this.handleLink.bind(this);
  }

  handleLink() {
    let link = document.getElementById('info-link');
    link.innerHTML = (link.innerHTML === 'More information') ? 'Less' : 'More information';
  }
  render() {
    return (
        <SlideToggle
          collapsed
          render={ ({onToggle, setCollapsibleElement}) => (
            <React.Fragment>
              <div className="info-wrapper pl-5">
                <a href="javascript:void(0)" id="info-link" onClick={(event) => { onToggle(); this.handleLink();}}>More information</a>
              </div>
              <div className="collapsible-content pl-5" ref={setCollapsibleElement}>
                <div className="collapsible-content-inner">
                  {this.props.children}
                </div>
              </div>
            </React.Fragment> )} />
    )
  } 
}


class ArtDisplay extends Component {
  render() {
    return(     
      <div className="d-flex">
          <div className="d-flex flex-column">
            <div className="artwork-container text-left pl-5">
              {this.props.children}
            </div>
          </div>
          <div className="d-flex flex-column">
            <div className="description pl-5">

              <p> TITLE: {this.props.title}</p>
              <p> CLIENT: {this.props.client}</p>
              <p> YEAR: {this.props.year}</p>
              <p> TECHNIQUE: {this.props.technique}</p>

            </div>
            <Description>
              {this.props.text}
            </Description>
          </div>
      </div>
    )
  }
}

export default ArtDisplay;
