import React, { Component } from 'react';
import './Header.css';
import HeaderLogo from '../assets/header.jpg';
import MenuIcon from '../assets/menu-icon.png';
import CloseIcon from '../assets/close-icon.png';


class Header extends Component {

  constructor() {
    super();
    this.menuSelect = this.menuSelect.bind(this);
    this.state = {
      src: MenuIcon
    }
  }

  menuSelect() {
    if (this.state.src === MenuIcon)
      this.setState({src: CloseIcon});
    else
      this.setState({src: MenuIcon});
  }

  render() {
    return(
      <div className="header d-md-none d-sm-flex align-items-center">
        <img className="menu d-block" src={this.state.src} onClick={this.menuSelect} height="60px"/>
        <img className="header-logo text-center d-block mx-auto" src={HeaderLogo} height="60px"/>
      </div>
    )
  }
}

export default Header;
