import React, { Component } from 'react';
import './Sidebar.css';
import logo from '../assets/logo.png';
import { NavLink } from 'react-router-dom';

const SidebarItem = (prop) => {
  return (
    <div className="Sidebar-item text-center d-none d-lg-block h2 col">
      <NavLink exact to={prop.link} activeStyle={{ textDecoration: "underline", textDecorationColor: "#F5D321" }}>
        {prop.name}
      </NavLink>  
    </div>
  );
}

const Logo = () => {
  return (
    <div className="logo text-center col">
      <img src={logo} height="220px" width="200px" alt="logo" />
    </div>
  )
}

class Sidebar extends Component {
  render() {
    return (
      <div className="navigation row mx-0">

        <SidebarItem link="/" name="artwork" />
        <SidebarItem link="/photo" name="photo" />
        <Logo />
        <SidebarItem link="/about" name="about" />
        <SidebarItem link="/contact" name="contact" />

      </div>
    );
  }
}

export default Sidebar;
