import React, { Component } from 'react';
import './Obruc.css';
import ObrucArtwork from '../assets/art/obruc.jpg';



function changeText(e) {
  e.preventDefault();
  let linkElement = document.getElementById("infoLink");
  linkElement.innerHTML = (linkElement.innerHTML === "More information") ? "Less" : "More information";
}

class Description extends Component {
  constructor() {
    super();
    this.state = {
      isClicked: false
    }
  }

  toggleClass() {
    this.setState( {
      isClicked: !this.state.isClicked
    });
  }

  render() {
    return (
      <div>
        <button onClick={this.toggleClass.bind(this)}>
          Click here
        </button>
        {!this.state.isClicked && <Child />}
      </div>
    )
  } 
}

const Child = () =>  {
  <div>
      What up!
  </div>
}

class Obruc extends Component {

  render() {
    return(
      <div className="d-flex">
        <div className="artwork text-left pl-5">
          <img src={ObrucArtwork} alt="Obruc" />
        </div>
        <div className="d-flex flex-column">
          <div className="description pl-5">
          <p>TITLE: Home in the Mountains</p>
          <p>CLIENT: Mountaineering Society Obruƒç Jelenje</p>
          <p>YEAR: 2018</p>
          <p>TECHNIQUE: Ink</p>
          </div>
          <div className="info-wrapper pl-5">
            <a href="javascript:void(0)" id="infoLink" onClick={changeText}>More information</a>
          </div>
        </div>
      </div>
    );
  }
}

export default Obruc;
