import React, { Component } from 'react';
import './Obruc.css';
import '../Components/ArtDisplay'
import ObrucColor from '../assets/art/obruc-color.jpg';
import ObrucGrey from '../assets/art/obruc-grey.jpg';
import ArtDisplay from '../Components/ArtDisplay';




function changeText(e) {
  e.preventDefault();
  let linkElement = document.getElementById("infoLink");
  linkElement.innerHTML = (linkElement.innerHTML === "More information") ? "Less" : "More information";
}

class Description extends Component {
  constructor() {
    super();
    this.state = {
      isClicked: false
    }
  }
  toggleClass() {
    this.setState( {
      isClicked: !this.state.isClicked
    });
  }

  render() {
    return (
      <div className="info-wrapper pl-5">
        <button onClick={this.toggleClass.bind(this)}>
          Click here
        </button>
        {!this.state.isClicked && <Child />}
      </div>
    )
  } 
}

const Child = () =>  (
  <div>
      What up!
  </div>
);

class Obruc extends Component {

  render() {
    return(
      <ArtDisplay title='Mountaineering Society Logo' client='P.D. Obruč - Jelenje' year='2018' technique='Mixed media'>
        <img className="block-image" src={ObrucColor}></img>
        <img className="block-image" src={ObrucGrey}></img>
      </ArtDisplay>
    );
  }
}

export default Obruc;
