import React, { Component } from 'react';
import { SlideToggle } from 'react-slide-toggle';
import './Header.css';
import HeaderLogo from '../assets/header.jpg';
import MenuIcon from '../assets/menu-icon.png';
import CloseIcon from '../assets/close-icon.png';


class Header extends Component {

  constructor() {
    super();
    this.menuSelect = this.menuSelect.bind(this);
    this.state = {
      src: MenuIcon
    }
  }

  menuSelect() {
    if (this.state.src === MenuIcon)
      this.setState({src: CloseIcon});
    else
      this.setState({src: MenuIcon});
  }

  render() {
    return(
      <div className="header d-md-none d-sm-block">
        <SlideToggle
          render={ ({onToggle, setCollapsibleElement}) => (
            <div myCollapsible>
            <img className="menu" src={this.state.src} onClick={onToggle, this.menuSelect} height="40px"/>
            <div className="my-collapsible__content" ref={setCollapsibleElement}>
              <div className="my-collapsible__content-inner">
                Collapsible content
              </div>
            </div>
            </div>
          )}
            />
        <img className="header-logo position-absolute" src={HeaderLogo} height="40px"/>
      </div>
    )
  }
}

export default Header;
