import React, { Component } from 'react';
import './ArtDisplay.css';


class Description extends Component {
  constructor() {
    super();
    this.state = {
      isClicked: false
    }
  }
  toggleClass(e) {
    this.setState( {
      isClicked: !this.state.isClicked
    });
  }

  changeText(e) {
    e.preventDefault();
    let linkElement = document.getElementById("infoLink");
    linkElement.innerHTML = (linkElement.innerHTML === "More information") ? "Less" : "More information";
  }

  render() {
    return (
      <div className="info-wrapper pl-5">
        <button onClick={(event) => {this.toggleClass.bind(this); this.changeText() }}>
          Click here
        </button>
        {!this.state.isClicked && <Child />}
      </div>
    )
  } 
}

const Child = () =>  (
  <div>
      What up!
  </div>
);

class ArtDisplay extends Component {
  render() {
    return(
      <div className="d-flex">
        <div className="d-flex flex-column">
          <div className="artwork-container text-left pl-5">
            {this.props.children}
          </div>
        </div>
        <div className="d-flex flex-column">
          <div className="description pl-5">

            <p> TITLE: {this.props.title}</p>
            <p> CLIENT: {this.props.client}</p>
            <p> YEAR: {this.props.year}</p>
            <p> TECHNIQUE: {this.props.technique}</p>

          </div>
          <Description />
        </div>
      </div>
    )
  }
}

export default ArtDisplay;
